import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap} from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios'; // Importa axios para hacer solicitudes HTTP
import carIconImg from '../../car.svg';
import datosGeoJSON from '../maps/datosGeoJSON'

function Mapa() {
    const [routeData, setRouteData] = useState(null); // Estado para almacenar los datos de la ruta

    useEffect(() => {
        // Llama a la función para obtener datos de la ruta cuando el componente se monta
        obtenerDatosDeRuta();
    }, []);

    // Función para obtener datos de la ruta utilizando OpenRouteService
    const obtenerDatosDeRuta = async () => {
        try {
            // Realiza una solicitud HTTP a OpenRouteService para obtener datos de la ruta
            const response = await axios.get('https://lxelctvrnx7xs4eoxujkgqxk6u0khkdy.lambda-url.us-east-1.on.aws/');
            // Establece los datos de la ruta en el estado
            setRouteData(response.data);
        } catch (error) {
            console.error('Error al obtener datos de la ruta:', error);
        }
    };

    // Define el icono del carro
    const carIcon = L.icon({
        iconUrl: carIconImg, // Ruta al icono de carro
        iconSize: [32, 32], // Tamaño del icono
    });

    // Función para trazar la ruta en el mapa
    const TrazarRuta = () => {
        const map = useMap();
        if (routeData) {
            // Crea un arreglo de coordenadas de la ruta
            const coordinates = routeData.features[0].geometry.coordinates;
            console.log('coordinates----->',coordinates)
            // Toma las coordenadas y crea una ruta
            const polyline = L.polyline(coordinates, { color: 'blue' }).addTo(map);
            // Ajusta el mapa para que se ajuste a la ruta
            map.fitBounds(polyline.getBounds());
        }
        return null;
    };

    return (
        <MapContainer center={[19.5, -99.14946]} zoom={13} style={{ height: '400px', width: '100%' }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            {/* Renderiza la ruta */}
            <TrazarRuta />
            {/* Renderiza el marcador de inicio */}
            <Marker position={[19.50047, -99.14946]} icon={carIcon}>
                <Popup>Inicio</Popup>
            </Marker>
            {/* Renderiza el marcador de fin */}
            <Marker position={[19.47101, -99.13781]} icon={carIcon}>
                <Popup>Fin</Popup>
            </Marker>
        </MapContainer>
    );
}

export default Mapa;
