import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import carIconImg from '../../car.svg';

function Mapa({ selectedVehicles }) {
    // Define el icono del carro
    const carIcon = L.icon({
        iconUrl: carIconImg, // Ruta al icono de carro
        iconSize: [32, 32], // Tamaño del icono
    });

    return (
        <MapContainer center={[19.5, -99.14946]} zoom={13} style={{ height: '400px', width: 'auto', margin: '33px' }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

            {/* Renderiza los marcadores para cada vehículo seleccionado */}
            {selectedVehicles.map((vehicle, index) => (
                <Marker key={index} position={[vehicle.latitude, vehicle.longitude]} icon={carIcon}>
                    <Popup>
                        <strong>
                            {vehicle.BRAND} {vehicle.MODEL} {vehicle.YEAR}<br />
                            Placa: {vehicle.placa}
                        </strong>
                    </Popup>
                </Marker>
            ))}
        </MapContainer>
    );
}

export default Mapa;
