import React, { useState } from 'react';
import axios from 'axios';

function VehicleForm() {
    const [formData, setFormData] = useState({
        BRAND: '',
        MODEL: '',
        YEAR: '',
        COLOR: '',
        placa: '',
        'numero economico': '',
        vim: '',
        asientos: '',
        seguro: '',
        'segure numebr': ''
    });

    const [emptyFields, setEmptyFields] = useState([]);
    const [successMessage, setSuccessMessage] = useState('');
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
        if (emptyFields.includes(e.target.name) && e.target.value !== '') {
            setEmptyFields(emptyFields.filter(field => field !== e.target.name));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const emptyFieldsArray = Object.keys(formData).filter(key => formData[key] === '');
        if (emptyFieldsArray.length > 0) {
            setEmptyFields(emptyFieldsArray);
        } else {
            setEmptyFields([]);
            setLoading(true);
            try {
                const response = await axios.post('https://www.api.merakimx.mx/gpd/vehicle/creates', formData);
                console.log('Datos del formulario enviados correctamente.');
                setSuccessMessage('¡Los datos del formulario se enviaron correctamente!');
                setFormData({ 
                    BRAND: '',
                    MODEL: '',
                    YEAR: '',
                    COLOR: '',
                    placa: '',
                    'numero economico': '',
                    vim: '',
                    asientos: '',
                    seguro: '',
                    'segure numebr': ''
                });
                setTimeout(() => {
                    window.location.href = '/'; 
                }, 2000);

            } catch (error) {
                console.error('Error al enviar los datos del formulario:', error);
            }
        }
    };


    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Alta de Vehículo</h2>
            <div style={{ margin: '7%' }}>
                {loading ? (
                    <div style={{ textAlign: 'center' }}>
                        <p>Enviando datos...</p>
                    </div>
                ) : (
                    <form
                        onSubmit={handleSubmit}
                        style={{
                            display: "grid",
                            gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
                            gap: "1rem",
                        }}
                    >
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="BRAND">Marca{emptyFields.includes('BRAND') && " *"}:</label>
                            <input
                                type="text"
                                id="BRAND"
                                name="BRAND"
                                value={formData.BRAND}
                                onChange={handleChange}
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('BRAND') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="MODEL">Modelo{emptyFields.includes('MODEL') && " *"}:</label>
                            <input
                                type="text"
                                id="MODEL"
                                name="MODEL"
                                value={formData.MODEL}
                                onChange={handleChange}
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('MODEL') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="YEAR">Año{emptyFields.includes('YEAR') && " *"}:</label>
                            <input
                                type="number"
                                id="YEAR"
                                name="YEAR"
                                value={formData.YEAR}
                                onChange={handleChange}
                                min="1900"
                                max="2099"
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('YEAR') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="COLOR">Color{emptyFields.includes('COLOR') && " *"}:</label>
                            <input
                                type="text"
                                id="COLOR"
                                name="COLOR"
                                value={formData.COLOR}
                                onChange={handleChange}
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('COLOR') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="placa">Placa{emptyFields.includes('placa') && " *"}:</label>
                            <input
                                type="text"
                                id="placa"
                                name="placa"
                                value={formData.placa}
                                onChange={handleChange}
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('placa') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="numero economico">Número Económico{emptyFields.includes('numero economico') && " *"}:</label>
                            <input
                                type="text"
                                id="numero economico"
                                name="numero economico"
                                value={formData['numero economico']}
                                onChange={handleChange}
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('numero economico') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="vim">VIM{emptyFields.includes('vim') && " *"}:</label>
                            <input
                                type="text"
                                id="vim"
                                name="vim"
                                value={formData.vim}
                                onChange={handleChange}
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('vim') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="asientos">Asientos{emptyFields.includes('asientos') && " *"}:</label>
                            <input
                                type="number"
                                id="asientos"
                                name="asientos"
                                value={formData.asientos}
                                onChange={handleChange}
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('asientos') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="seguro">Seguro{emptyFields.includes('seguro') && " *"}:</label>
                            <input
                                type="text"
                                id="seguro"
                                name="seguro"
                                value={formData.seguro}
                                onChange={handleChange}
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('seguro') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="segure numebr">Número de Seguro{emptyFields.includes('segure numebr') && " *"}:</label>
                            <input
                                type="text"
                                id="segure numebr"
                                name="segure numebr"
                                value={formData['segure numebr']}
                                onChange={handleChange}
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('segure numebr') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        <div style={{ gridColumn: "1 / -1", textAlign: "center" }}>
                            <button type="submit" style={{ color: 'white', textDecoration: 'none', padding: '10px 20px', borderRadius: '5px', backgroundColor: '#007bff', border: 'aliceblue', }}>Enviar</button>
                        </div>
                    </form>)}
                {successMessage && (
                    <div style={{ color: 'green', marginTop: '10px' }}>
                        {successMessage}
                    </div>
                )}
            </div>
        </div>
    );
}

export default VehicleForm;
