import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import carIconImg from '../../car.svg';

function Mapa({ vehicles }) {
    // Estado para almacenar los vehículos seleccionados
    const [selectedVehicles, setSelectedVehicles] = useState([]);

    // Define el icono del carro
    const carIcon = L.icon({
        iconUrl: carIconImg,
        iconSize: [32, 32],
    });

    // Función para trazar la ruta en el mapa
    const TrazarRutas = ({ vehiculos }) => {
        const map = useMap();

        // Borra todas las capas de ruta antes de trazar las nuevas
        map.eachLayer(layer => {
            if (layer instanceof L.Polyline) {
                map.removeLayer(layer);
            }
        });

        // Trazar las rutas de los vehículos seleccionados
        vehiculos.forEach(vehiculo => {
            const { init, end } = vehiculo;
            const polyline = L.polyline([init, end], { color: 'blue' }).addTo(map);
            map.fitBounds(polyline.getBounds());
        });

        return null;
    };

    // Efecto para actualizar los vehículos seleccionados y trazar las rutas
    useEffect(() => {
        if (vehicles) {
            // Filtrar los vehículos seleccionados
            const newSelectedVehicles = vehicles.filter(vehiculo => vehiculo.selected);
            // Actualizar el estado de los vehículos seleccionados
            setSelectedVehicles(newSelectedVehicles);
        }
    }, [vehicles]);

    return (
        <MapContainer center={[19.5, -99.14946]} zoom={13} style={{ height: '400px', width: 'auto', margin: '33px' }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

            {/* Renderizar las rutas */}
            {selectedVehicles.length > 0 && <TrazarRutas vehiculos={selectedVehicles} />}

            {/* Renderizar los marcadores de vehículos */}
            {selectedVehicles.map(vehiculo => (
                <div key={vehiculo._id}>
                    <Marker position={vehiculo.init} icon={carIcon}>
                        <Popup>
                            Inicio - <strong>{vehiculo.BRAND} {vehiculo.MODEL} {vehiculo.BRAND}</strong>
                            Placa: <strong>{vehiculo.placa}</strong>
                        </Popup>
                    </Marker>
                    <Marker position={vehiculo.end} icon={carIcon}>
                        <Popup>
                            Fin - <strong>{vehiculo.BRAND} {vehiculo.MODEL} {vehiculo.BRAND}</strong>
                            Placa: <strong>{vehiculo.placa}</strong>
                        </Popup>
                    </Marker>
                </div>
            ))}
        </MapContainer>
    );
}

export default Mapa;
