import React from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

function Mapa({ datosGeoJSON }) {
    console.log('datosGeoJSON----',datosGeoJSON);
  return (
    <MapContainer center={[51.505, -0.09]} zoom={13} style={{ height: '400px', width: '100%' }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      {/* Renderizar los marcadores */}
      {datosGeoJSON.map((data, index) => (
        <Marker key={index} position={[data.geometry.coordinates[1], data.geometry.coordinates[0]]}>
          <Popup>{data.properties.name}</Popup>
        </Marker>
      ))}
      {/* Buscar y renderizar la lÃ­nea */}
      {datosGeoJSON.map((data, index) => (
        data.geometry.type === 'LineString' && (
          <Polyline key={index} positions={data.geometry.coordinates} />
        )
      ))}
    </MapContainer>
  );
}

export default Mapa;
