import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';


function VehicleUpdateForm({ vehicleId }) {
    const { id } = useParams();
    console.log('ID del vehículo:', id);
    vehicleId=id;
    const [formData, setFormData] = useState({
        BRAND: '',
        MODEL: '',
        YEAR: '',
        COLOR: '',
        placa: '',
        'numero economico': '',
        vim: '',
        asientos: '',
        seguro: '',
        'segure numebr': ''
    });

    const [emptyFields, setEmptyFields] = useState([]);
    const [successMessage, setSuccessMessage] = useState('');
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        // Aquí puedes hacer una solicitud para obtener los datos del vehículo con el ID proporcionado
        const fetchVehicleData = async () => {
            try {
                const response = await axios.get(`https://www.api.merakimx.mx/gpd/vehicle/ads/${vehicleId}`);
                const vehicleData = response.data;
                setFormData(vehicleData);
            } catch (error) {
                console.error('Error al obtener los datos del vehículo:', error);
            }
        };

        fetchVehicleData();
    }, [vehicleId]);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
        if (emptyFields.includes(e.target.name) && e.target.value !== '') {
            setEmptyFields(emptyFields.filter(field => field !== e.target.name));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const emptyFieldsArray = Object.keys(formData).filter(key => formData[key] === '');
        if (emptyFieldsArray.length > 0) {
            setEmptyFields(emptyFieldsArray);
        } else {
            setEmptyFields([]);
            setLoading(true);
            try {
                const response = await axios.put(`https://www.api.merakimx.mx/gpd/vehicle/${vehicleId}`, formData);
                console.log('Datos del formulario actualizados correctamente.');
                setSuccessMessage('¡Los datos del formulario se actualizaron correctamente!');
                setTimeout(() => {
                    window.location.href = '/'; 
                }, 2000);

            } catch (error) {
                console.error('Error al enviar los datos del formulario:', error);
            } finally {
                setLoading(false);
            }
        }
    };


    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Actualización de Vehículo</h2>
            <div style={{ margin: '7%' }}>
                {loading ? (
                    <div style={{ textAlign: 'center' }}>
                        <p>Enviando datos...</p>
                    </div>
                ) : (
                    <form
                        onSubmit={handleSubmit}
                        style={{
                            display: "grid",
                            gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))",
                            gap: "1rem",
                        }}
                    >
                        <div style={{ display: "grid", gridTemplateRows: "auto 1fr", gap: "0.5rem" }}>
                            <label htmlFor="BRAND">Marca{emptyFields.includes('BRAND') && " *"}:</label>
                            <input
                                type="text"
                                id="BRAND"
                                name="BRAND"
                                value={formData.BRAND}
                                onChange={handleChange}
                                required
                                style={{ height: '25px' }}
                            />
                            {emptyFields.includes('BRAND') && <span style={{ color: 'red' }}>Campo requerido</span>}
                        </div>
                        {/* Repite los demás campos del formulario con los nombres correspondientes */}
                        <div style={{ gridColumn: "1 / -1", textAlign: "center" }}>
                            <button type="submit" style={{ color: 'white', textDecoration: 'none', padding: '10px 20px', borderRadius: '5px', backgroundColor: '#007bff', border: 'aliceblue', }}>Enviar</button>
                        </div>
                    </form>)}
                {successMessage && (
                    <div style={{ color: 'green', marginTop: '10px' }}>
                        {successMessage}
                    </div>
                )}
            </div>
        </div>
    );
}

export default VehicleUpdateForm;
