import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import carIconImg from '../../car.svg';
//import datosGeoJSON from '../maps/datosGeoJSON'

function Mapa({ vehicle }) {
    const [routeData, setRouteData] = useState(null); // Estado para almacenar los datos de la ruta
    console.log('vehicle---->>>>>', vehicle)
    // Define el icono del carro
    const carIcon = L.icon({
        iconUrl: carIconImg, // Ruta al icono de carro
        iconSize: [32, 32], // Tama침o del icono
    });

    // Funci칩n para trazar la ruta en el mapa
    const TrazarRuta = () => {
        const map = useMap();

        const coordinates = [
            [19.5, -99.14946], // Coordenadas del primer punto (latitud, longitud)
            [19.47101, -99.13781],   // Coordenadas del segundo punto (latitud, longitud)
            [19.37101, -99.12781],
        ];
        const polyline = L.polyline(coordinates, { color: 'blue' }).addTo(map);
        // Ajusta el mapa para que se ajuste a la ruta
        map.fitBounds(polyline.getBounds());
        return null;
    };

    return (
        <MapContainer center={[19.5, -99.14946]} zoom={13} style={{ height: '400px', width: 'auto', margin: '33px' }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

            <TrazarRuta />
            {/* Renderiza el marcador de inicio */}
            <Marker position={[19.50047, -99.14946]} icon={carIcon}>
                <Popup>
                    Inicio - <strong> {vehicle && vehicle.BRAND} {vehicle && vehicle.MODEL} {vehicle && vehicle.BRAND}  </strong>
                    Placa: <strong>{vehicle.placa} </strong>

                </Popup>
            </Marker>
            {/* Renderiza el marcador de fin */}
            <Marker position={[19.47101, -99.13781]} icon={carIcon}>
                <Popup>Fin</Popup>
            </Marker>
        </MapContainer>
    );
}

export default Mapa;
