import React from 'react';
import axios from 'axios';

function VehicleCard({ vehicle, onVehicleDelete }) {
    const handleDelete = async () => {
        try {
            await axios.delete(`http://localhost:3006/ad/${vehicle._id}`, {
                //await axios.delete(`https://www.api.merakimx.mx/gpd/vehicle/ad/${vehicle._id}`, {
                data: {
                    status: false
                }
            });
            console.log('Eliminación exitosa');
            onVehicleDelete(); // Llama a la función para limpiar selectedVehicle en Home
        } catch (error) {
            console.error('Error al eliminar el vehículo:', error);
            // Aquí puedes manejar el error, por ejemplo, mostrando un mensaje al usuario
        }
    };

    const handleUpdate = async () => {
        const vehicleId = vehicle._id;
        window.location.href = `/update?id=${vehicleId}`;
    }

    return (
        <div style={{ position: 'relative', marginBottom: '20px', padding: '10px', border: '1px solid #ccc', borderRadius: '5px', overflow: 'hidden' }}>

            <div>
                <Link
                    to="/alta"
                    style={{
                        position: 'absolute',
                        top: '5px',
                        right: '80px',
                        background: 'blue',
                        color: 'white',
                        border: 'none',
                        padding: '5px',
                        cursor: 'pointer',
                        textDecoration: 'none', // Elimina la subrayado del enlace
                        borderRadius: '5px'
                    }}
                >
                    Actualizar
                </Link>
            </div>
            <button
                onClick={handleDelete}
                style={{ position: 'absolute', top: '5px', right: '5px', background: 'red', color: 'white', border: 'none', padding: '5px', cursor: 'pointer' }}
            >

                Eliminar
            </button>
            <h3>{vehicle.BRAND} {vehicle.MODEL}</h3>
            <p><strong>Año:</strong> {vehicle.YEAR}</p>
            <p><strong>Color:</strong> {vehicle.COLOR}</p>
            <p><strong>Placa:</strong> {vehicle.placa}</p>
            <p><strong>Número Económico:</strong> {vehicle['numero economico']}</p>
            <p><strong>VIN:</strong> {vehicle.vim}</p>
            <p><strong>Asientos:</strong> {vehicle.asientos}</p>
            <p><strong>Seguro:</strong> {vehicle.seguro}</p>
            <p><strong>Número de Seguro:</strong> {vehicle['segure numebr']}</p>
        </div>


    );
}

export default VehicleCard;
