import React, { useState, useEffect } from 'react';
import imgCar from '../../resources/Comfort_Premium.png';

function VehicleList({ onSelectVehicle }) {
    const [vehicles, setVehicles] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:3006/ads?page=1');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setVehicles(data); // Actualiza el estado con los datos de los vehículos
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleSelectVehicle = (vehicle) => {
        onSelectVehicle(vehicle); // Envía los datos del vehículo seleccionado al componente padre
    };

    // Función para manejar el cambio en el término de búsqueda
    const handleSearchChange = (event) => {
        setSearchTerm(event.target.value);
    };

    // Filtrar los vehículos según el término de búsqueda
    const filteredVehicles = vehicles.filter((vehicle) =>
        vehicle.BRAND.toLowerCase().includes(searchTerm.toLowerCase()) ||
        vehicle.MODEL.toLowerCase().includes(searchTerm.toLowerCase()) ||
        vehicle.YEAR.toString().includes(searchTerm.toLowerCase()) ||
        vehicle.COLOR.toLowerCase().includes(searchTerm.toLowerCase()) ||
        vehicle.vim.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div style={{ margin: '30px' }}>
            <h2>Lista de Vehículos</h2>
            {/* Barra de búsqueda */}
            <input
                type="text"
                placeholder="Buscar vehículo..."
                value={searchTerm}
                onChange={handleSearchChange}
                style={{ marginBottom: '10px', padding: '5px', width: '100%' }}
            />
            <ul style={{ listStyle: 'none', padding: 0 }}>
                {filteredVehicles.map(vehicle => (
                    <li key={vehicle.id} style={{ margin: '10px 0', width: '100%', marginBottom: ' 8%', height: "100px" }}>
                        <button onClick={() => handleSelectVehicle(vehicle)} style={{ padding: '5px 10px', border: '1px solid #ccc', borderRadius: '5px', cursor: 'pointer', backgroundColor: '#f0f0f0', display: 'flex', alignItems: 'center', width: '100%', background: 'white' }}>
                            <div style={{ display: 'flex', flexDirection: 'row', width: '100%' }}>
                                <div style={{ flex: '1', minWidth: '30%' }}>
                                    {/* Imagen del vehículo */}
                                    <img src={imgCar} alt="Vehicle" style={{ width: '100px', height: '100px' }} />
                                </div>
                                <div style={{ flex: '1', minWidth: '70%', paddingLeft: '10px', alignItems: 'flex-start', justifyContent: 'flex-start' }}>
                                    <div style={{ fontSize: '18px', margin: 0, textAlign: 'left', marginBottom: '5px' }}>
                                        <strong>{vehicle.BRAND} {vehicle.MODEL} {vehicle.YEAR}</strong>
                                    </div>
                                    <div style={{ margin: 0, textAlign: 'left', marginBottom: '5px' }}>
                                        <strong>Color:</strong> {vehicle.COLOR}
                                    </div>
                                    <div style={{ margin: 0, textAlign: 'left', marginBottom: '5px' }}>
                                        <strong>VIN:</strong> {vehicle.vim}
                                    </div>
                                    <div style={{ margin: 0, textAlign: 'left', marginBottom: '5px' }}>
                                        <strong>Seguro:</strong> {vehicle.seguro}
                                    </div>
                                </div>

                            </div>
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default VehicleList;
