import React, { useState } from 'react';

function VehicleForm() {
    const [formData, setFormData] = useState({
        brand: '',
        model: '',
        year: '',
        color: '',
        plate: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Aquí puedes hacer lo que quieras con los datos del formulario
        console.log('Datos del formulario:', formData);
        // Por ejemplo, puedes enviarlos a un servidor o almacenarlos localmente
    };

    return (
        <div>
            <h2>Formulario de Vehículo</h2>
            <form
                onSubmit={handleSubmit}
                style={{
                    display: "flex",
                    flexDirection: "column",
                    gap: "1rem",
                    maxWidth: "300px", // Ancho máximo para evitar que los elementos se extiendan demasiado
                }}
            >
                <div>
                    <label htmlFor="brand">Marca:</label>
                    <input
                        type="text"
                        id="brand"
                        name="brand"
                        value={formData.brand}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="model">Modelo:</label>
                    <input
                        type="text"
                        id="model"
                        name="model"
                        value={formData.model}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="year">Año:</label>
                    <input
                        type="number"
                        id="year"
                        name="year"
                        value={formData.year}
                        onChange={handleChange}
                        min="1900"
                        max="2099"
                        required
                    />
                </div>
                <div>
                    <label htmlFor="color">Color:</label>
                    <input
                        type="text"
                        id="color"
                        name="color"
                        value={formData.color}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="plate">Matrícula:</label>
                    <input
                        type="text"
                        id="plate"
                        name="plate"
                        value={formData.plate}
                        onChange={handleChange}
                        required
                        style={{ width: "100%" }}
                    />
                </div>
            </form>


            <button type="submit">Enviar</button>

        </div>
    );
}

export default VehicleForm;
