import React, { useState } from 'react';
import Mapa from './Mapa';
import VehicleList from './VehicleList';
import datosGeoJSON from './datosGeoJSON';
import './App.css'; // Importa estilos CSS

function App() {
  const [menuOpen, setMenuOpen] = useState(false); // Estado para controlar si el menú está abierto o cerrado

  // Función para alternar el estado del menú
  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className="app-container">
      {/* Menú lateral */}
      <div className={`menu ${menuOpen ? 'open' : ''}`}>
        {/* Contenido del menú aquí */}
        <button onClick={toggleMenu}>Cerrar</button>
      </div>

      {/* Contenido principal */}
      <div className="main-content">
        {/* Encabezado con botón para abrir el menú */}
        <header>
          <button onClick={toggleMenu}>Abrir Menú</button>
        </header>

        {/* Mapa y lista de vehículos */}
        <div className="content">
          <Mapa datosGeoJSON={datosGeoJSON} />
          <VehicleList />
        </div>
      </div>
    </div>
  );
}

export default App;
