import React from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import carIconImg from '../../car.svg';

function Mapa({ vehicles }) {
    console.log('vehicles--------',vehicles[0].init)
    const carIcon = L.icon({
        iconUrl: carIconImg,
        iconSize: [32, 32],
    });

    const TrazarRutas = ({ vehiculos }) => {
        const map = useMap();
        vehiculos.forEach(vehiculo => {
            const { init, end } = vehiculo;
            const polyline = L.polyline([init, end], { color: 'blue' }).addTo(map);
            map.fitBounds(polyline.getBounds());
        });

        return null;
    };

    return (
        <MapContainer center={vehicles[0].init} zoom={13} style={{ height: '400px', width: 'auto', margin: '33px' }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

            {vehicles && vehicles.length > 0 && ( // Validación de vehicles
                <TrazarRutas vehiculos={vehicles} />
            )}

            {/* Validación de vehicles antes de renderizar los marcadores */}
            {vehicles && vehicles.length > 0 && vehicles.map(vehiculo => (
                <div key={vehiculo._id}>
                    <Marker position={vehiculo.init} icon={carIcon}>
                        <Popup>
                            Inicio - <strong>{vehiculo.placa}</strong>
                        </Popup>
                    </Marker>
                    <Marker position={vehiculo.end} icon={carIcon}>
                        <Popup>
                            Fin - <strong>{vehiculo.placa}</strong>
                        </Popup>
                    </Marker>
                </div>
            ))}
        </MapContainer>
    );
}

export default Mapa;
