import React, { useState, useEffect } from 'react';
import imgCar from '../../resources/Comfort_Premium.png';

function VehicleList({ onSelectVehicle }) {
    const [vehicles, setVehicles] = useState([]);
    const [selectedVehicle, setSelectedVehicle] = useState(null); // Estado para almacenar el vehículo seleccionado

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:3006/ads?page=1');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setVehicles(data); // Actualiza el estado con los datos de los vehículos
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleSelectVehicle = (vehicle) => {
        setSelectedVehicle(vehicle); // Actualiza el vehículo seleccionado
        onSelectVehicle(vehicle); // Envía los datos del vehículo seleccionado al componente padre
    };

    // Determinar si el vehículo actual está seleccionado
    const isSelected = (vehicle) => {
        return selectedVehicle === vehicle;
    };

    // CSS para el botón cuando está seleccionado
    const selectedButtonStyle = {
        borderRadius: '5px', // Establecer el radio del borde redondeado
    };

    // Función para aplicar el estilo del botón dependiendo de si está seleccionado
    const getButtonStyle = (vehicle) => {
        if (isSelected(vehicle)) {
            return { ...selectedButtonStyle }; // Aplicar el estilo cuando está seleccionado
        } else {
            return {}; // No aplicar ningún estilo cuando no está seleccionado
        }
    };

    return (
        <div style={{ margin: '30px' }}>
            <h2>Lista de Vehículos</h2>
            <ul style={{ listStyle: 'none', padding: 0 }}>
                {vehicles.map(vehicle => (
                    <li key={vehicle.id} style={{ margin: '10px 0', width: '100%', marginBottom: '8%', height: "100px" }}>
                        <button
                            onClick={() => handleSelectVehicle(vehicle)}
                            style={{
                                padding: '5px 10px',
                                border: '1px solid #ccc',
                                cursor: 'pointer',
                                backgroundColor: '#f0f0f0',
                                display: 'flex',
                                alignItems: 'center',
                                width: '100%',
                                background: 'white',
                                ...getButtonStyle(vehicle), // Aplicar el estilo del botón dependiendo de si está seleccionado
                            }}
                        >
                            {/* División izquierda del botón con la imagen */}
                            <div style={{ marginRight: '10px' }}>
                                {/* Imagen del vehículo */}
                                <img src={imgCar} alt="Vehicle" style={{ width: '100px', height: '100px' }} />
                            </div>
                            {/* División derecha del botón con la información del vehículo */}
                            <div style={{ marginLeft: 'auto' }}>
                                <strong>Marca:</strong> {vehicle.BRAND}, <strong>Modelo:</strong> {vehicle.MODEL}, <strong>Año:</strong> {vehicle.YEAR}, <strong>-</strong> {vehicle.COLOR}
                            </div>
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default VehicleList;
