import React, { useState } from 'react';
import axios from 'axios';
import './vehicle.css'; // Importa los estilos CSS

function VehicleForm() {
    const [formData, setFormData] = useState({
        BRAND: '',
        MODEL: '',
        YEAR: '',
        COLOR: '',
        placa: '',
        'numero economico': '',
        vim: '',
        asientos: '',
        seguro: '',
        'segure numebr': ''
    });

    const [emptyFields, setEmptyFields] = useState([]);
    const [successMessage, setSuccessMessage] = useState('');
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
        if (emptyFields.includes(e.target.name) && e.target.value !== '') {
            setEmptyFields(emptyFields.filter(field => field !== e.target.name));
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const emptyFieldsArray = Object.keys(formData).filter(key => formData[key] === '');
        if (emptyFieldsArray.length > 0) {
            setEmptyFields(emptyFieldsArray);
        } else {
            setEmptyFields([]);
            setLoading(true);
            try {
                const response = await axios.post('https://www.api.merakimx.mx/gpd/vehicle/creates', formData);
                console.log('Datos del formulario enviados correctamente.');
                setSuccessMessage('¡Los datos del formulario se enviaron correctamente!');
                setFormData({ 
                    BRAND: '',
                    MODEL: '',
                    YEAR: '',
                    COLOR: '',
                    placa: '',
                    'numero economico': '',
                    vim: '',
                    asientos: '',
                    seguro: '',
                    'segure numebr': ''
                });
                setTimeout(() => {
                    window.location.href = '/'; 
                }, 2000);

            } catch (error) {
                console.error('Error al enviar los datos del formulario:', error);
            }
        }
    };


    return (
        <div className="vehicle-form-container">
            <h2>Alta de Vehículo</h2>
            <div className="form-wrapper">
                {loading ? (
                    <div className="loading-indicator">
                        <p>Enviando datos...</p>
                    </div>
                ) : (
                    <form onSubmit={handleSubmit} className="form">
                        <div className="form-item">
                            <label htmlFor="BRAND">Marca{emptyFields.includes('BRAND') && " *"}:</label>
                            <input
                                type="text"
                                id="BRAND"
                                name="BRAND"
                                value={formData.BRAND}
                                onChange={handleChange}
                                required
                                className={emptyFields.includes('BRAND') ? 'input-error' : ''}
                            />
                            {emptyFields.includes('BRAND') && <span className="error-message">Campo requerido</span>}
                        </div>
                        {/* Resto de los campos del formulario */}
                        <div className="form-item">
                            <button type="submit" className="submit-button">Enviar</button>
                        </div>
                    </form>
                )}
                {successMessage && (
                    <div className="success-message">
                        {successMessage}
                    </div>
                )}
            </div>
        </div>
    );
}

export default VehicleForm;
