import React, { useState } from 'react';
import Tab from './Tab';
import TabPanel from './TabPanel';
import EditForm from './EditForm';
import DeleteForm from './DeleteForm';
import UpdateForm from './UpdateForm';

function App() {
  const [selectedTab, setSelectedTab] = useState('edit');

  const handleTabChange = (tab) => {
    setSelectedTab(tab);
  };

  return (
    <div className="App">
      <h1>Mi aplicación con pestañas</h1>
      <div className="Tabs">
        <Tab label="Editar" selected={selectedTab === 'edit'} onClick={() => handleTabChange('edit')} />
        <Tab label="Eliminar" selected={selectedTab === 'delete'} onClick={() => handleTabChange('delete')} />
        <Tab label="Actualizar" selected={selectedTab === 'update'} onClick={() => handleTabChange('update')} />
      </div>
      <div className="TabPanels">
        {selectedTab === 'edit' && <TabPanel><EditForm /></TabPanel>}
        {selectedTab === 'delete' && <TabPanel><DeleteForm /></TabPanel>}
        {selectedTab === 'update' && <TabPanel><UpdateForm /></TabPanel>}
      </div>
    </div>
  );
}

export default App;
