import React, { useState, useEffect } from 'react';
import imgCar from '../../resources/Comfort_Premium.png';

function VehicleList({ onSelectVehicle }) {
    const [vehicles, setVehicles] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:3006/ads?page=1');
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setVehicles(data); // Actualiza el estado con los datos de los vehículos
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const handleSelectVehicle = (vehicle) => {
        onSelectVehicle(vehicle); // Envía los datos del vehículo seleccionado al componente padre
    };

    return (
        <div style={{ margin: '30px' }}>
            <h2>Lista de Vehículos</h2>
            <ul style={{ listStyle: 'none', padding: 0 }}>
                {vehicles.map(vehicle => (
                    <li key={vehicle.id} style={{ margin: '10px 0', width: '100%', marginBottom: ' 8%', height: "100px" }}>
                        <button onClick={() => handleSelectVehicle(vehicle)} style={{ padding: '5px 10px', border: '1px solid #ccc', borderRadius: '5px', cursor: 'pointer', backgroundColor: '#f0f0f0', display: 'flex', alignItems: 'center', width: '100%', background: 'white' }}>
                            <div style={{ display: 'flex', flexDirection: 'row' }}>
                                <div style={{ marginRight: '10px' ,flex: '1', minWidth: '40%'}}>
                                    {/* Imagen del vehículo */}
                                    <img src={imgCar} alt="Vehicle" style={{ width: '100px', height: '100px' }} />
                                </div>
                                <div style={{ display: 'flex', flex: '1', minWidth: '60%' }}>
                                    <div>
                                        <strong>{vehicle.BRAND} {vehicle.MODEL} {vehicle.YEAR}</strong>
                                    </div>
                                    <div>
                                        VIN:  {vehicle.vim}
                                    </div>
                                </div>
                            </div>

                            {/* División derecha del botón con la información del vehículo */}

                        </button>

                    </li>
                ))}
            </ul>
        </div>
    );
}

export default VehicleList;
