// Home.js

import React, { useState } from 'react';
import VehicleList from './VehicleList';

function Home() {
    const [selectedVehicle, setSelectedVehicle] = useState(null);

    const handleVehicleSelection = (vehicle) => {
        setSelectedVehicle(vehicle);
    };

    return (
        <div>
            <div>
                <h1>Home</h1>
                {/* Pasamos la función de manejo de selección de vehículo a VehicleList */}
                <VehicleList onSelectVehicle={handleVehicleSelection} />
            </div>
            <div>
                {/* Aquí puedes mostrar la información del vehículo seleccionado */}
                {selectedVehicle && (
                    <div>
                        <h2>Información del vehículo seleccionado:</h2>
                        <p>ID: {selectedVehicle.id}</p>
                        <p>Nombre: {selectedVehicle.name}</p>
                        {/* Aquí puedes mostrar más detalles del vehículo */}
                    </div>
                )}
            </div>
        </div>
    );
}

export default Home;
